  function GiraldoType(){
    let eaChatbotText=document.getElementById("divChatBotGiraldoText");
    if(eaChatbotText==null){return false;}
    let eaChatbotUserText=document.getElementById("txtChatBotUserText");
    if(eaChatbotUserText==null){return false;}
    if(event.key=="Enter"){
      let sGiraldoResponse=eaChatbotUserText.value;
      eaChatbotUserText.value="";
      GiraldoWriteText(true,sGiraldoResponse);
      GiraldoResponse(sGiraldoResponse);
    }
  }

  function GiraldoWriteText(bUserOrGiraldo,sTextToWrite){
    let eaChatbotText=document.getElementById("divChatBotGiraldoText");
    if(eaChatbotText==null){return false;}
    let eaChatbotUserText=document.getElementById("txtChatBotUserText");
    if(eaChatbotUserText==null){return false;}
    let sGiraldoWriteText="";
    if(bUserOrGiraldo==true){
      sGiraldoWriteText+="<p style='color:" + sGiraldoUserColor + ";margin:15px;text-shadow:" + sGiraldoTextShadowSize + " " + sGiraldoUserTextShadow + ";'>" + sGiraldoUser + ": " + sTextToWrite + "</p>";
    }else{
      sGiraldoWriteText+="<p style='color:" + sGiraldoSelfColor + ";margin:15px;text-shadow:" + sGiraldoTextShadowSize + " " + sGiraldoSelfTextShadow + "'>" + sGiraldoName + ": " + sTextToWrite + "</p>";
    }
    eaChatbotText.innerHTML+=sGiraldoWriteText;
    eaChatbotText.scrollTo(0,eaChatbotText.scrollHeight);
    eaChatbotUserText.focus();
  }

