  function Giraldo() {
    let eaChatbotDiv=document.getElementById("divChatBotGiraldo");
    if(eaChatbotDiv==null){return false;}
    let eaChatbotField=document.getElementById("divChatBotGiraldoField");
    if(eaChatbotField==null){return false;}
    let eaChatbotText=document.getElementById("divChatBotGiraldoText");
    if(eaChatbotText==null){return false;}
    let eaChatbotCloseImage=document.getElementById("imgChatBotCloseImage");
    if(eaChatbotCloseImage==null){return false;}
    let eaChatbotTitleBar=document.getElementById("divChatBotTitleBar");
    if(eaChatbotTitleBar==null){return false;}
    let eaChatbotUserText=document.getElementById("txtChatBotUserText");
    if(eaChatbotUserText==null){return false;}
    if(eaChatbotField.style.height=="0px"){
      eaChatbotField.style.transition="height 2s ease 0s";
      eaChatbotField.style.height="400px";
      eaChatbotText.style.transition="height 2s ease 1s";
      eaChatbotText.style.height="290px";
      eaChatbotCloseImage.style.transition="height 1s ease 2s"; 
      eaChatbotCloseImage.style.height="10px";
      eaChatbotTitleBar.style.transition="height 1s ease 2s,bottom 1s ease 1s,right 1s ease 1s";
      eaChatbotTitleBar.style.height="20px";
      eaChatbotTitleBar.style.right="10px";
      eaChatbotTitleBar.style.bottom="390px";
      eaChatbotUserText.style.transition="height 1s ease 1s,bottom 1s ease 1s";
      eaChatbotUserText.style.height="20px";
      eaChatbotUserText.style.bottom="20px";
      if(eaChatbotText.innerHTML==""){
        window.setTimeout(function(){GiraldoWriteText(false,sGiraldoIntroMessage);},2100);
      }else{
        eaChatbotText.scrollTo(0,eaChatbotText.scrollHeight);
      }
      window.setTimeout(function(){document.getElementById("txtChatBotUserText").focus();},2000);
    }else{
      eaChatbotField.style.transition="height 2s ease 0.6s";
      eaChatbotField.style.height="0px";
      eaChatbotText.style.transition="height 0.4s ease 0s";
      eaChatbotText.style.height="0px";
      eaChatbotCloseImage.style.transition="height 0.4s ease 0s"; 
      eaChatbotCloseImage.style.height="0px";
      eaChatbotTitleBar.style.transition="height 0.4s ease 0s";
      eaChatbotTitleBar.style.transition="height 1s ease 2s,bottom 1s ease 1s,right 1s ease 1s";
      eaChatbotTitleBar.style.height="20px";
      eaChatbotTitleBar.style.right="-200px";
      eaChatbotTitleBar.style.bottom="120px";
      eaChatbotUserText.style.transition="height 0.4s ease 0s,bottom 0.4s ease 0s";
      eaChatbotUserText.style.height="0px";
      eaChatbotUserText.style.bottom="-25px";
    }
  }
  function connectPersonnel(){
    let eaChatbotText=document.getElementById("divChatBotGiraldoText");
    if(eaChatbotText==null){return false;}
    GiraldoWriteText(false,sGiraldoAnswerSpeakEmployee);
    window.setTimeout(function(){GiraldoResponse(sGiraldoNoConnectionEmployee1);},2000);
  }

  
